export function generateLetters(length: number): string[] {
    let letters: string[] = [];

    for (let i = 0; i < length; i++) {
        letters[i] = WEIGHTED_LETTERS[(Math.floor(Math.random() * WEIGHTED_LETTERS.length))];
    }

    return letters;
}

const length = 8;
const duration = 10;

export function generateGame(target_users: User[]): AnagramObject {
    const uuid: string = v4();
    const letters = generateLetters(length);

    const game: AnagramObject = {
        uuid: uuid,
        states: {},
        config: {
            letters: letters,
            duration: duration
        },
        users: []
    };

    for (const user of target_users) {
        addUserToGame(game, user);
    }

    return game;
}

export function addUserToGame(game: AnagramObject, user: User): AnagramObject {
    const state: AnagramState = {
        words: [],
        stage: 'NOT-STARTED',
        score: 0
    };

    game.states[user.user_id] = state;

    game.users.push(user);

    return game;
}

