export function generateLetters(length: number): string[] {
    let letters: string[] = [];

    for (let i = 0; i < length; i++) {
        letters[i] = WEIGHTED_LETTERS[(Math.floor(Math.random() * WEIGHTED_LETTERS.length))];
    }

    return letters;
}

const length = 8;
const duration = 5;

export function generateGame(target_users: User[]): Partial<AnagramObject> {
    const uuid: string = v4();
    const letters = generateLetters(length);

    const game: Partial<AnagramObject> = {
        states: {},
        config: {
            letters: letters,
            duration: duration
        },
        users: []
    };

    for (const user of target_users) {
        game.states[user.user_id] = defaultState;
        game.users.push(user);
    }

    return game;
}

export const defaultState: AnagramState = {
    words: [],
    stage: 'NOT-STARTED',
    score: 0,
    viewed: false
};

